import axios from "axios";
import React, { useState } from "react";
import { connect } from "react-redux";
import './blog-detail.scss';

const BlogDetail = props => {
    const [showComment,toggleComment] = useState(false);
    const [commentsData,updateComments] = useState([]);

    const toggleSection = () => {
        if(commentsData.length){
            toggleComment(!showComment);
        }else{
            axios.get(
                'https://jsonplaceholder.typicode.com/comments?postId='+props.blogData.id
            ).then(res => {
                updateComments(res.data);
                toggleComment(true);
            })
        }
    }

    const CommentSection = () => {
        let data = commentsData.map(cur => {
            return (
                <div className={'comment-box'}>
                    <div className={'user-name'}>{cur.name}</div>
                    <div className={'email-txt'}>{cur.email}</div>
                    <div className={'comment-body'}>{cur.body}</div>
                </div>
            )
        });
        return data;
    }

    return (
        <div className={'main-container'}>
            <div className={'slide-up'}>
                <div className={'heading'}>
                    <h1>{'Blog id: '+props.blogData.id}</h1>
                </div>
                <div className={'title'}>{props.blogData.title}</div>
                <div className={'post'}>
                    {props.blogData.body}
                    {showComment ? 
                        <div className={'comment-sec'}>
                            {'COMMENTS:'}
                            <CommentSection />
                        </div>:null}

                    <div className={'show-hide-sec'}>
                        <div className={'btn'} onClick={() => toggleSection()}>
                            {showComment?'Hide Comments':'Show Comments'}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
};

const mapStateToProps = state => {
    return {
        blogData: state.blogData?.blogDetail
    }
};

export default connect(mapStateToProps)(BlogDetail);
