import React from "react";
import Slider from "react-slick";
import { connect } from 'react-redux';
import './my-preference.scss';
import { Button } from '@mui/material';

const HomeSlider = (props) => {
        const settings = {
          dots: true,
          infinite: true,
          speed: 500,
          slidesToShow: 3,
          slidesToScroll: 1
        };
        return (
          <div>
        <div className="myPreferences-sec">
            <h1>My Prefercences</h1>
            <Slider {...settings}>
            {
            props.homesliderimages.map((image , index)=> {
                    return (
                    <div key={index}>
                    <div className="sec-left">
                    <img src={require('../assets/'+image.imgPath).default}/>
                    </div>
                    <div className="sec-right slidertext">
                    <h1>{image.imgText}<span>{image.imgText1}</span></h1>
                    <Button 
                    variant="outlined"
                    className="slider-btn"
                >
                    Assign
                </Button>
                    </div>
                    </div>
                    )
            })}
            </Slider>
            </div>
          </div>
        );
      }


const mapStateToProps = state => {
        return {
            homesliderimages: state.foodData.homeslider
        }
}
export default connect(mapStateToProps)(HomeSlider);
