import React, { useEffect } from 'react';
import './App.css';
import axios from 'axios';
import { setListingData } from './actions/index';
import { connect } from 'react-redux';
import Listing from './listing/Listing';
import {
	BrowserRouter as Router,
	Route,
	Switch
} from 'react-router-dom';
import BlogDetail from './app/blog/BlogDetail';


function App(props) {

  useEffect(() => {
    if(!props.listingData.length && window.location.pathname == '/'){
      axios.get('https://jsonplaceholder.typicode.com/posts').then(res => {
        props.setListingData(res.data);
      })
    }
    if(window.location.pathname != '/'){
      document.title = props.blogDetail.title
    }
  },[window.location.pathname])

  return (
    <Router>
      <div className="App">
        <Switch>
          <Route exact path='/' component={Listing}></Route>
          <Route exact path='/blog/:id' component={BlogDetail}></Route>
          {/* <Route exact path='/contact' component={Contact}></Route> */}
        </Switch>
      </div>
    </Router>
  );
}

const mapStateToProps = state => {
  return {
    listingData: state.blogData.listingData,
    blogDetail: state.blogData.blogDetail
  }
};

export default connect(mapStateToProps,{setListingData})(App);
