import React, { useState } from 'react';
import './add-form.scss';

const AddForm = props => {
    const [countryObj,updateCountry] = useState({
        name: '',
        continent: '',
        rank: '',
        flag: ''
    });
    const handleChange = (value,key) => {
        let countryData = {...countryObj};
        countryData[key] = value;
        updateCountry(countryData)
    }
    return (
        <div className='add-form'>
            <input type="text" placeholder='Enter Name' className='input-holder' value={countryObj.name} onChange={e => handleChange(e.target.value,'name')} />
            <input type="number" placeholder="Enter Rank" className='input-holder' value={countryObj.rank} onChange={e => handleChange(e.target.value,'rank')}/>
            <input type="text" placeholder="Enter Continent" className='input-holder' value={countryObj.continent} onChange={e => handleChange(e.target.value,'continent')} />
            <input type="file" />
        </div>
    )
};

export default AddForm;
