import React, { useEffect, useState } from "react";
import { connect } from "react-redux";
import { withRouter } from "react-router-dom";
import { setBlogDetail } from "../actions";
import './listing.scss';

const Listing = props => {

    const [listingData,updateList] = useState(props.listingData);
    useEffect(() => {
        if(!listingData.length && props.listingData.length){
            updateList(props.listingData);
        }
    },[props.listingData])
    const SingleData = (data) => {
        return (
            <li className={'single-data'} key={data.blogData.id} onClick={() => {
                props.setBlogDetail(data.blogData);
                props.history.push('/blog/'+data.blogData.id)
            }}>
                {data.blogData.title}
                <div className={'via-user'}>{':By user - '+data.blogData.userId}</div>
            </li>
        )
    }

    const handleChange = (e) => {
        let srchTerm = e.target.value;
        if(srchTerm.length > 2) {
            let resultArr = props.listingData.filter(cur => {
                if(cur.title.indexOf(srchTerm) != -1){
                    return true;
                }
            });
            console.log(resultArr);
            updateList(resultArr);
        }else if(listingData.length != props.listingData.length){
            updateList(props.listingData);
        }
    }
    return (
        <div className={'main-container'}>
            <div className={'heading'}>
                <h1>Welcome to CAWstudio Blogs</h1>
            </div>
            <div className={'heading list-heading'}>
                <h1>Blog List: </h1>
                <input className={'search-input'} type={Text} placeholder={'Search by title'} onChange={handleChange}/>
            </div>
            <ol className={'gradient-list'}>
                {listingData.length?props.listingData.map((cur,index) => {
                    return (
                        <SingleData blogData={cur} index={index} />
                    )
                }):null}
            </ol>
        </div>
    );
}; 

const mapStateToProps = state => {
    return {
        listingData: state.blogData?.listingData
    }
}

export default connect(mapStateToProps,{setBlogDetail})(withRouter(Listing));
