import React from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import { setBlogDetail } from "../actions";
import './listing.scss';

const Listing = props => {

    const SingleData = (data) => {
        return (
            <li className={'single-data'} onClick={() => {
                props.setBlogDetail(data);
            }}>
                <Link to={'/blog'} >
                    {data.title}
                </Link>
            </li>
        )
    }

    return (
        <div className={'main-container'}>
            <div className={'heading'}>
                <h1>Welcome to Anshul's Blogs</h1>
            </div>
            <div className={'heading list-heading'}>
                <h1>Blog List: </h1>
            </div>
            <ol className={'gradient-list'}>
                {props.listingData.length?props.listingData.map((cur,index) => {
                    return (
                        <SingleData title={cur.title} index={index} />
                    )
                }):null}
            </ol>
        </div>
    );
}; 

const mapStateToProps = state => {
    return {
        listingData: state.blogData?.listingData
    }
}

export default connect(mapStateToProps,{setBlogDetail})(Listing);
