import React, { useState } from 'react';
import { connect } from 'react-redux';
import './main.scss';

const Main = props => {
    const [selectedCountry, updateCountry] = useState(0);
    const onSelectCountry = (e) => {
        console.log(e.target.value);
    }
    return (
        <div className='main-container'>
            <div className='content'>
                <select className='dropdown' placeholder='Select Country' onChange={onSelectCountry} value={selectedCountry}>
                    <option value={0} className={'placeholder'}>Select Country</option>
                    {
                        props.countryData.map((cur,index) => {
                            return (
                                <option value={index + 1}>{cur.name}</option>
                            )
                        })
                    }
                </select>
                {selectedCountry?
                    <div className='selected-country'>
                        <div className='heading'>Selected Country Details: </div>
                    </div>
                :null}
            </div>
        </div>
    )
};

const mapStateToProps = (state)  => {
    return {
        countryData: state.countryData.countryData
    }
}
export default connect(mapStateToProps)(Main);
