const initialState = {
    listingData: [],
    blogDetail: {}
};

export default (state = initialState, action) => {
    switch(action.type){
        case 'SET_LISTING_DATA' : {
            return {
                ...state,
                listingData: [...action.payload]
            }
        }
        case 'SET_BLOG_DETAIL': {
            return {
                ...state,
                blogDetail: {...action.payload}
            }
        }
        default: {
            return state;
        }
    }
};
