import React, { useState } from "react";
import { connect } from "react-redux";
import './blog-detail.scss';

const BlogDetail = props => {
    const [showComment,toggleComment] = useState(false);

    return (
        <div className={'main-container'}>
            <div className={'heading'}>
                <h1>{'Blog id: '+props.blogData.id}</h1>
            </div>
            <div className={'title'}>{props.blogData.title}</div>
            <div className={'post'}>
                {props.blogData.body}
            </div>
            {!showComment?
                <div className={'comment-txt'}>Show Commnets</div>:null}
        </div>
    )
};

const mapStateToProps = state => {
    return {
        blogData: state.blogData?.blogDetail
    }
};

export default connect(mapStateToProps)(BlogDetail);
