import React, { useState } from 'react';
import './add-form.scss';

const AddForm = props => {
    const [countryObj,updateCountry] = useState({
        name: '',
        continent: '',
        rank: '',
        flag: ''
    })
    return (
        <div className='add-form'>
            <input type="text" className='input-holder' value={countryObj.name} />
            <input type="text" className='input-holder' value={countryObj.rank} />
            <input type="number" className='input-holder' value={countryObj.continent} />
            <input type="file" />
        </div>
    )
};

export default AddForm;
