import React, { useState } from 'react';
import './add-form.scss';

const AddForm = props => {
    const [countryObj,updateCountry] = useState({
        name: '',
        continent: '',
        rank: '',
        flag: ''
    });
    const [erros,updateErrors] = useState({});
    const handleChange = (value,key) => {
        let countryData = {...countryObj};
        countryData[key] = value;
        updateCountry(countryData)
    }
    return (
        <div className='add-form'>
            <div className='input-holder'>
                <label className={'label-txt '+(countryObj.name?'on-top':'')}>Country Name</label>
                <input type="text" placeholder='Country Name' className='' value={countryObj.name} onChange={e => handleChange(e.target.value,'name')} />
            </div>
            <div className='input-holder'>
                <label className={'label-txt '+(countryObj.rank?'on-top':'')}>Country Rank</label>
                <input type="number" placeholder="Country Rank" className='' value={countryObj.rank} onChange={e => handleChange(e.target.value,'rank')}/>            </div>
            <div className='input-holder'>
                <label className={'label-txt '+(countryObj.continent?'on-top':'')}>Continent Name</label>
                <input type="text" placeholder="Continent Name" className='' value={countryObj.continent} onChange={e => handleChange(e.target.value,'continent')} />
            </div>
            <input type="file" />
            <button className='btn'>Save</button>
        </div>
    )
};

export default AddForm;
